/**
 * Tailwind CSS config file.
 *
 * @since 1.0.0
 *
 * @internal Tailwind doesn't know to look for `.tailwindrc.cjs` files, unfortunately.
 * If you ever need to call tailwind directly be sure to tell it where the config file lives.
 * It's not an issue at this time because we don't call it directly. This file is require()'d elsewhere.
 *
 * @internal PLEASE DO NOT EDIT THIS FILE!
 * This file and the contents of it are updated automatically.
 * Instead of editing, please configure `composer.json`. See instructions below.
 * Instead of editing, please see source repository @ <https://git.io/JD8Zo>.
 */

/* @formatter:ignore
-----------------------------------------------------------------------------------------------------------------------
Example `extra.props-dev.tailwind` in `composer.json`:
-----------------------------------------------------------------------------------------------------------------------
"extra" : {
	"props-dev" : {
		"tailwind" : {
			"theme" : {
				"fontFamily" : {
					"sans"  : [ "Georama", "sans-serif" ],
					"serif" : [ "Georgia", "serif" ]
				}
			}
		}
	}
}
-----------------------------------------------------------------------------------------------------------------------
Example `index.scss` starter file contents:
-----------------------------------------------------------------------------------------------------------------------
@tailwind base;
@tailwind components;
@tailwind utilities;
@import 'https://fonts.googleapis.com/css2?family=Georama:ital,wght@0,100..900;1,100..900&display=swap';
------------------------------------------------------------------------------------------------- @formatter:/ignore */

const path = require( 'path' );
const mc   = require( '@clevercanyon/js-object-mc' );

module.exports = ( () => {
	const file = {
		'composer.json' : {},
		'package.json'  : {},
	};
	try { file[ 'composer.json' ] = require( '../composer.json' ); } catch ( e ) {}
	try { file[ 'package.json' ] = require( '../package.json' ); } catch ( e ) {}

	return mc.merge(
		{
			theme   : {
				fontFamily : {
					sans  : [ 'Georama', 'sans-serif' ],
					serif : [ 'Georgia', 'serif' ],
				},
			},
			content : [ './src/{**/*,**/.*,.*,*}.{md,xml,htm,html,php,jsx,js,cjs,tsx,ts,cts}' ],
		},
		file[ 'composer.json' ]?.extra?.[ 'props-dev' ]?.tailwind || {},
		file[ 'package.json' ]?.config?.[ 'props-dev' ]?.tailwind || {},
	);
} )();
